mapper.select:
SELECT tami_app_ip as tamiAppIp, tami_cpu_count as tamiCpuCount, tami_cpu_load as tamiCpuLoad, tami_sys_memory as tamiSysMemory, tami_sys_avail_memory as tamiSysAvailMemory, tami_app_use_memory as tamiAppUseMemory, tami_app_heap_max_memory as tamiAppHeapMaxMemory, tami_app_heap_min_memory as tamiAppHeapMinMemory, tami_app_heap_use_memory as tamiAppHeapUseMemory, tami_app_nonheap_max_memory as tamiAppNoheapMaxMemory, tami_app_nonheap_min_memory as tamiAppNoheapMinMemory, tami_app_nonheap_use_memory as tamiAppNoheapUseMemory, gmt_modify as gmtModify FROM trace_app_mermory_info WHERE tami_app_ip = #{appIp} ORDER BY gmt_create DESC
LIMIT #{limit};
LIMIT 7;

SELECT tati_app_ip as tatiAppIp, tati_thread_id as tatiThreadId, tati_thread_name as tatiThreadName, tati_thread_status as tatiThreadStatus, tati_thread_start_mtime as tatiThreadStartMtime, tati_thread_blocked_count as tatiThreadBlockedCount, tati_thread_blocked_mtime as tatiThreadBlockedMtime, tati_thread_blocked_name as tatiThreadBlockedName, tati_thread_blocked_id as tatiThreadBlockedId, tati_thread_waited_count as tatiThreadWaitedCount, tati_thread_waited_mtime as tatiThreadWaitedMtime, tati_thread_max_count as tatiThreadMaxCount, tati_thread_stack as tatiThreadStack, gmt_modify as gmtModify FROM trace_app_thread_info
tati_app_ip = #{appIp}
and tati_thread_name = #{threadName} LIMIT 1

SELECT thf_req_ip as thfReqIp, thf_url as thfUrl, thf_takeup_time as thfTakeupTime, thf_req_timestamp as thfReqTimestamp, thf_result_sucess as thfResultSucess, thf_input as thfInput, thf_result as thfResult, gmt_modify as gmtModify FROM trace_http_flow WHERE thf_req_ip = #{appIp} order by gmt_modify desc
LIMIT #{limit};
LIMIT 7;

SELECT tmf_topic as tmfReqIp, tmf_req_ip as tmfTopic, tmf_tag as tmfTag, tmf_msg_id as tmfMsgId, tmf_mq_content as tmfMqContent, tmf_resp_ip as tmfRespIp, tmf_producer_start_timestamp as tmfProducerStartTimestamp, tmf_consumer_end_timestamp as tmfConsumerStartTimestamp, tmf_consumer_takeup_timestamp as tmfConsumerTakeupTimestamp, gmt_modify as gmtModify FROM trace_mq_flow
tmf_req_ip = #{appIp}
and tmf_topic = #{topic}
and tmf_msg_id = #{msgId}
LIMIT #{limit};
LIMIT 7;

SELECT tss_req_ip as tssReqIp, tss_slow_sql as tssSlowSql, tss_take_time as tssTakeTime, tss_explain as tssExplain, tss_explain_content as tssExplainContent, gmt_modify as gmtModify FROM trace_slow_sql WHERE tss_req_ip = #{appIp} order by gmt_modify desc
LIMIT #{limit};
LIMIT 7;
