mapper.select:
SELECT thf_req_ip as thfReqIp, thf_url as thfUrl, thf_takeup_time as thfTakeupTime, thf_req_timestamp as thfReqTimestamp, thf_result_sucess as thfResultSucess, thf_input as thfInput, thf_result as thfResult, gmt_modify as gmtModify FROM trace_http_flow WHERE thf_req_ip = #{appIp} order by gmt_modify desc
LIMIT #{limit};
LIMIT 7;

SELECT tmf_topic as tmfTopic, tmf_req_ip as tmfReqIp, tmf_tag as tmfTag, tmf_msg_id as tmfMsgId, tmf_mq_content as tmfMqContent, tmf_resp_ip as tmfRespIp, tmf_producer_start_timestamp as tmfProducerStartTimestamp, tmf_consumer_end_timestamp as tmfConsumerStartTimestamp, tmf_consumer_takeup_timestamp as tmfConsumerTakeupTimestamp, gmt_modify as gmtModify FROM trace_mq_flow
tmf_req_ip in
#{ip}
and tmf_topic = #{topic}
and tmf_msg_id = #{msgId}
LIMIT #{limit}

select
    #{srvCode} as 'srvCode',
    #{srvName} as 'srvName',
    sli_qps_limit as qpsLimit,
    sli_fallback as fallback
from service_limit_info
where sli_service_bean_name = #{srvCode}
and sli_service_name = #{srvName}
limit 1;

SELECT tss_req_ip as tssReqIp, tss_slow_sql as tssSlowSql, tss_take_time as tssTakeTime, tss_explain as tssExplain, tss_explain_content as tssExplainContent, gmt_modify as gmtModify FROM trace_slow_sql WHERE tss_req_ip in
#{ip}
order by gmt_modify desc
